mapper.select:
select count(*)
from uc_member m 
where m.id = #{currentMemberId,jdbcType=INTEGER} and m.password = #{encryptedPassword,jdbcType=VARCHAR}

  	select count(*) from uc_member m
  	
  		, uc_member_group mg
  	
  	
  		
			mg.member_id = m.id
		    AND mg.group_code in
		    #{groupCode}
		
		
		    AND acc_no like #{accNo,jdbcType=VARCHAR}
		
		
		    AND name like #{name,jdbcType=VARCHAR}
		
		
		    AND id = #{memberId}

select m.acc_no, m.name, m.status, g.group_name, g.group_code, m.id, m.create_time, m.update_time, (select t.acc_no from uc_member t where t.id = m.creater_id) creater_acc_no, (select t.acc_no from uc_member t where t.id = m.modifier_id) modifier_acc_no from uc_member m, uc_member_group mg, uc_group g where mg.member_id(+) = m.id and mg.group_code = g.group_code(+)
AND m.id = #{memberCriteriaModel.memberId}
and (
mg.group_code in #{groupCode}
AND m.acc_no like #{memberCriteriaModel.accNo,jdbcType=VARCHAR}
AND m.name like #{memberCriteriaModel.name,jdbcType=VARCHAR}
) order by
m.status desc, m.id asc m.${pageHelper.sort} ${pageHelper.order}
