mapper.select:
  	    select * from rptchart t where 1=1
  	    
	  	    and t.toolflag=#{toolFlag, jdbcType=VARCHAR}
	  		
  	    
	  	    and t.chart_name like CONCAT('%', #{chartName,jdbcType=VARCHAR}, '%')

  	    select count(1) from rptchart t where 1=1
  	    
	  	    and t.toolflag=#{toolFlag, jdbcType=VARCHAR}
	  	
  	    
	  	    and t.chart_name like CONCAT('%', #{chartName,jdbcType=VARCHAR}, '%')
