select ur.id, ur.role_code, ur.name, ur.sys_code, ur.status, ur.create_time, ur.update_time, ur.description, (select t.acc_no from uc_member t where t.id = ur.creater_id) creater_acc_no, (select t.acc_no from uc_member t where t.id = ur.modifier_id) modifier_acc_no from uc_role ur where ur.sys_code in #{sysCode} and (
ur.role_code like #{roleCriteriaModel.roleCode,jdbcType=VARCHAR}
AND ur.name like #{roleCriteriaModel.roleName,jdbcType=VARCHAR}
) order by
ur.status desc, ur.id desc ur.${pageHelper.sort} ${pageHelper.order}

